# Evalscript to return NDWI
ndwi_eval = """
//VERSION=3
function setup() {
  return {
    input: ["B1", "B3"],
    output: { bands: 1, sampleType: "FLOAT32" }, 
  };
}

function evaluatePixel(sample) {
  let ndwi = (sample.B1 - sample.B3) / (sample.B1 + sample.B3)
  return [ndwi];
}
"""

# Build request for the first time step
